import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../assets/logo-modified.png';
import anaemiaImage from '../assets/anaemia.jpg';

function LandingPage() {
    const navigate = useNavigate();

    useEffect(() => {
        window.scrollTo(0, 0); // Scrolls the page to the top when the component mounts
    }, []);

    const handleSubmit = (event) => {
        event.preventDefault();
        // handle form submission here
    };

    return (
        <>
            <style>
                {`
                    @import 'tailwindcss/base';
                    @import 'tailwindcss/components';
                    @import 'tailwindcss/utilities';
                `}
            </style>
            <header className="sticky top-0 z-10 bg-[#4D869C]">
                <div className="mx-auto flex max-w-7xl items-center justify-between px-4 py-6 sm:px-6 lg:px-8">
                    <div className="flex flex-1 items-center">
                        <img src={logo} alt="Logo" className="h-10" />
                        <h2 className="ml-2 text-xl font-bold text-white">Anaemia Predictor</h2>
                    </div>
                    <div className="flex flex-1 items-center justify-end">
                        <button
                            onClick={() => navigate("/signup")}
                            className="bg-gradient-to-r from-[#102C57] to-[#2196F3] mr-2 px-4 py-2 text-sm font-medium text-white hover:bg-gradient-to-r hover:from-[#2196F3] hover:to-[#3F51B5]"
                        >
                            Sign Up
                        </button>
                        <button
                            onClick={() => navigate("/signin")}
                            className="bg-gradient-to-r from-[#102C57] to-[#2196F3] px-4 py-2 text-sm font-medium text-white hover:bg-gradient-to-r hover:from-[#2196F3] hover:to-[#3F51B5]"
                        >
                            Sign In
                        </button>
                    </div>
                </div>
            </header>

            <main className="flex min-h-screen flex-col items-center justify-center bg-[#FAFAFA]">
                <div className="mt-20 flex flex-col items-center justify-center md:mt-0 md:flex-row md:space-x-10">
                    <div className="mb-10 flex w-full items-center justify-center md:mb-0 md:w-1/2">
                        <img src={anaemiaImage} alt="Anaemia Prediction" className="rounded-lg" />
                    </div>
                    <div className="w-full px-4 md:w-1/2">
                        <h1 className="mb-4 text-center text-3xl font-bold text-[#102C57] md:text-left">Welcome to Anaemia Predictor</h1>
                        <p className="mb-4 text-center text-lg md:text-left">
                            This is a website where you can upload your image and we will predict whether you have anemia or not. Our machine learning model is trained to detect anemia from images.
                        </p>
                        <p className="mb-4 text-center text-base md:text-left">
                            Anemia is a condition in which you lack enough healthy red blood cells to carry adequate oxygen to your body's tissues. Having anemia can make you feel tired and weak. There are many forms of anemia, each with its own cause. Anemia can be temporary or long term, and it can range from mild to severe.
                        </p>
                        <p className="mb-4 text-center md:text-left">
                            Our model is designed to detect anemia by analyzing the color of the inner eyelid. This method is non-invasive and can be done easily at home. Please make sure to upload a clear and well-lit image for accurate results.
                        </p>
                        <button
                            onClick={() => navigate("/signin")}
                            className="bg-gradient-to-r from-[#102C57] to-[#2196F3] mx-auto block w-fit px-4 py-2 text-sm font-medium text-white hover:bg-gradient-to-r hover:from-[#2196F3] hover:to-[#3F51B5]"
                        >
                            Try it Now
                        </button>
                    </div>
                </div>
            </main>

            <footer className="bg-[#4D869C] px-4 py-2 text-center text-white">
                <p>&copy; {new Date().getFullYear()} | NanoBiosLab</p>
            </footer>
        </>
    );
}

export default LandingPage;
