import React, { useState } from 'react';
import axios from 'axios';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useNavigate } from 'react-router-dom';
import { AppBar, Toolbar, Card, CardContent } from '@mui/material';

const theme = createTheme();

function Predict() {
  const navigate = useNavigate();
  const [image, setImage] = useState(null);
  const [prediction, setPrediction] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };

  const captureImage = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      const video = document.createElement('video');
      video.srcObject = stream;
      video.play();

      video.onloadedmetadata = () => {
        const canvas = document.createElement('canvas');
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
        canvas.getContext('2d').drawImage(video, 0, 0);
        stream.getTracks().forEach(track => track.stop());
        const imageUrl = canvas.toDataURL('image/png');
        setImage(imageUrl);
      };
    } catch (error) {
      console.error(error);
      setError(error.toString());
    }
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    setError(null);
  
    const formData = new FormData();
    if (image instanceof File) {
      formData.append('image', image);
    } else {
      const blob = await fetch(image).then(r => r.blob());
      formData.append('image', blob);
    }
  
    axios.post('http://localhost:8000/api/predict/', formData)
      .then(response => {
        setPrediction(response.data.prediction);
        setLoading(false);
      })
      .catch(error => {
        setError(error.toString());
        setLoading(false);
      });
  };
  
  return (
    <>
      <AppBar position="static" sx={{ backgroundColor: "grey" }}>
        <Toolbar sx={{ justifyContent: "space-between" }}>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Anaemia-Predictor
          </Typography>
          <Box display="flex" justifyContent="space-between">
            <Button
              onClick={() => navigate("/signin")}
              variant="contained"
              sx={{ backgroundColor: "white", marginRight: "1rem" }}
              style={{ backgroundColor: 'blue' }}
            >
              Logout
            </Button>
            <Button
              onClick={() => navigate("/contact")}
              variant="contained"
              sx={{ backgroundColor: "#fff", marginLeft: "1rem" }}
              style={{ backgroundColor: 'blue' }}
            >
              Contact Us
            </Button>
          </Box>
        </Toolbar>
      </AppBar>

      <ThemeProvider theme={theme}>
        <Container maxWidth="md" sx={{ marginTop: 4 }}>
          <Box display="flex" justifyContent="space-between">
            {/* Sample Image Card */}
            <Card sx={{ width: "45%", borderRadius: "10px", boxShadow: "0 4px 8px rgba(0,0,0,0.1)" }}>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  Sample Image to be Uploaded
                </Typography>
                <img src="/sample-image.jpg" alt="Sample" style={{ width: '100%', marginTop: "1rem", borderRadius: "5px" }} />
              </CardContent>
            </Card>

            {/* Anaemia Detection Section */}
            <Box sx={{
              width: "45%", textAlign: 'center',
              backgroundColor: "#f5f5f5", padding: "2rem", borderRadius: "10px", boxShadow: "0 4px 8px rgba(0,0,0,0.1)"
            }}>
              <Typography variant="h4" gutterBottom>
                Anaemia Detection
              </Typography>

              <form onSubmit={handleFormSubmit} style={{ marginBottom: "1rem" }}>
                <input type="file" onChange={handleImageChange} style={{ display: 'none' }} id="image-upload" />
                <label htmlFor="image-upload">
                  <Button variant="contained" component="span" sx={{ marginRight: "1rem" }}>
                    Upload Image
                  </Button>
                </label>
                <Button onClick={captureImage} variant="contained" sx={{ marginRight: "1rem" }}>
                  Capture Image
                </Button>
                <Button type="submit" variant="contained" disabled={loading}>
                  {loading ? 'Uploading...' : 'Predict'}
                </Button>
              </form>

              {image && (
                <div>
                  <Typography variant="h5" sx={{ mt: 4 }}>Image Preview</Typography>
                  {typeof image === 'string' ? (
                    <img src={image} alt="Preview" style={{ maxWidth: '100%', marginTop: "1rem", borderRadius: "5px" }} />
                  ) : (
                    <img src={URL.createObjectURL(image)} alt="Preview" style={{ maxWidth: '100%', marginTop: "1rem", borderRadius: "5px" }} />
                  )}
                </div>
              )}

              {loading && (
                <div>
                  <Typography variant="h5" sx={{ mt: 4 }}>Loading...</Typography>
                </div>
              )}

              {prediction !== null && !loading && (
                <div>
                  <Typography variant="h5" sx={{ mt: 4 }}>Prediction</Typography>
                  <Typography variant="body1">{prediction === 0 ? 'Normal' : 'Anaemia'}</Typography>
                </div>
              )}

              {error && !loading && (
                <div>
                  <Typography variant="h5" sx={{ mt: 4 }}>Error</Typography>
                  <Typography variant="body1">{error}</Typography>
                </div>
              )}
            </Box>
          </Box>
        </Container>
      </ThemeProvider>
    </>
  );
}

export default Predict;
