import React from 'react';
import { Container, Typography, Button, AppBar, Toolbar, Grid, useMediaQuery, CssBaseline } from '@mui/material';
import { useTheme } from '@mui/material/styles';
import { useNavigate } from 'react-router-dom';

function LandingPage() {
  const navigate = useNavigate();
  const theme = useTheme();
  const matchesSM = useMediaQuery(theme.breakpoints.down('sm'));

  return (
    <>
      <CssBaseline />
      <AppBar position="static" style={{ backgroundColor: '#2196F3' }}>
        {/* Your AppBar content */}
      </AppBar>
      <Container maxWidth="lg" style={{ backgroundImage: `url('https://source.unsplash.com/random/?health,medical')`, backgroundSize: 'cover', minHeight: '100vh', padding: '50px' }}>
        <Grid container spacing={2} justifyContent="center" alignItems="center" style={{ minHeight: '80vh' }}>
          <Grid item xs={12} sm={6}>
            <Typography variant={matchesSM ? "h5" : "h3"} align="center" gutterBottom style={{ color: '#3F51B5' }}>
              Welcome to Anaemic-Prediction
            </Typography>
            <Typography variant="body1" align="center" paragraph>
              This is a website where you can upload your image and we will predict whether you have anemia or not. Our machine learning model is trained to detect anemia from images.
            </Typography>
            <Typography variant="body1" align="center" paragraph>
              Anemia is a condition in which you lack enough healthy red blood cells to carry adequate oxygen to your body's tissues. Having anemia can make you feel tired and weak. There are many forms of anemia, each with its own cause. Anemia can be temporary or long term, and it can range from mild to severe.
            </Typography>
            <Typography variant="body1" align="center" paragraph>
              Our model is designed to detect anemia by analyzing the color of the inner eyelid. This method is non-invasive and can be done easily at home. Please make sure to upload a clear and well-lit image for accurate results.
            </Typography>
            <Button
              variant="contained"
              color="primary"
              onClick={() => navigate("/predict")}
              sx={{
                background: "linear-gradient(45deg, #2196F3 30%, #64B5F6 90%)",
                boxShadow: "0px 3px 5px 2px rgba(33, 150, 243, .3)",
                color: "white",
                "&:hover": {
                  background: "linear-gradient(45deg, #64B5F6 30%, #2196F3 90%)",
                  boxShadow: "0px 5px 10px 2px rgba(33, 150, 243, .3)",
                },
              }}
            >
              Try it Now
            </Button>
          </Grid>
          <Grid item xs={12} sm={6}>
            <img src="https://miro.medium.com/v2/resize:fit:1400/1*Z-Aqguy-l1cve-gDupTG7A.jpeg" alt="Anaemia Prediction" style={{ width: '100%', height: 'auto' }} />
          </Grid>
        </Grid>
      </Container>
    </>
  );
}

export default LandingPage;
