import React, { useState } from 'react';
import axios from 'axios';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useNavigate } from 'react-router-dom';
import { AppBar, Toolbar } from '@mui/material';

const theme = createTheme();

function Predict() {
  const navigate = useNavigate();
  const [image, setImage] = useState(null);
  const [prediction, setPrediction] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };

  const captureImage = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      const video = document.createElement('video');
      video.srcObject = stream;
      video.play();

      const canvas = document.createElement('canvas');
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      canvas.getContext('2d').drawImage(video, 0, 0);
      stream.getTracks().forEach(track => track.stop());

      setImage(canvas.toDataURL('image/png'));
    } catch (error) {
      console.error(error);
      setError(error.toString());
    }
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    setError(null);

    const formData = new FormData();
    if (image instanceof File) {
      formData.append('image', image);
    } else {
      const blob = await fetch(image).then(r => r.blob());
      formData.append('image', blob);
    }

    axios.post('http://localhost:8000/api/predict/', formData)
      .then(response => {
        setPrediction(response.data.prediction);
        setLoading(false);
      })
      .catch(error => {
        setError(error.toString());
        setLoading(false);
      });
  };

  return (
    <>
      <AppBar position="static">
        <Toolbar sx={{ justifyContent: "space-between" }}>
          <h1>Anaemia-Predictor</h1>
          <Box display="flex" justifyContent="space-between">
            <Button
              onClick={() => navigate("/contact")}
              style={{ backgroundColor: "white", marginLeft: "1rem" }}
            >
              Contact Us
            </Button>
            <Button
              onClick={() => navigate("/signin")}
              style={{ backgroundColor: "white", marginRight: "1rem" }}
            >
              Logout
            </Button>

          </Box>
        </Toolbar>
      </AppBar>

      <ThemeProvider theme={theme}>
        <Container maxWidth="sm">
          <Box sx={{
            marginTop: 8, textAlign: 'center',
            backgroundColor: "#ececec"
          }}>
            <Typography variant="h4" gutterBottom>
              Anaemia Detection
            </Typography>

            <form onSubmit={handleFormSubmit}>
              <input type="file" onChange={handleImageChange} />
              <Button onClick={captureImage} variant="contained" sx={{ mt: 2 }}>
                Capture Image
              </Button>
              <Button type="submit" variant="contained" disabled={loading} sx={{ mt: 2 }}>
                {loading ? 'Uploading...' : 'Upload Image'}
              </Button>
            </form>

            {image && (
              <div>
                <Typography variant="h5" sx={{ mt: 4 }}>Image Preview</Typography>
                {typeof image === 'string' ? (
                  <img src={image} alt="Preview" style={{ maxWidth: '100%' }} />
                ) : (
                  <img src={URL.createObjectURL(image)} alt="Preview" style={{ maxWidth: '100%' }} />
                )}
              </div>
            )}

            {loading && (
              <div>
                <Typography variant="h5" sx={{ mt: 4 }}>Loading...</Typography>
              </div>
            )}

            {prediction !== null && !loading && (
              <div>
                <Typography variant="h5" sx={{ mt: 4 }}>Prediction</Typography>
                <Typography variant="body1">{prediction === 0 ? 'Normal' : 'Anaemia'}</Typography>
              </div>
            )}

            {error && !loading && (
              <div>
                <Typography variant="h5" sx={{ mt: 4 }}>Error</Typography>
                <Typography variant="body1">{error}</Typography>
              </div>
            )}
          </Box>
        </Container>
      </ThemeProvider>
    </>
  );
}

export default Predict;
